import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  // ** Alternative **
  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);

    // ** Alternative **
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value,
    // });

    // **Use this function syntax whenever state update depends on the previous state**
    // setUserInput((prevState) => {
    //   return { ...prevState, enteredTitle: event.target.value };
    // });
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);

    // ** Alternative **
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value,
    // });
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);

    // ** Alternative **
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value,
    // });
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };

  const toggleAddExpense = () => {
    return (
      <button type="submit" onClick={(viewForm = true)}>
        Add New Expense
      </button>
    );
  };

  const showExpenseForm = () => {
    return (
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              value={enteredTitle}
              onChange={titleChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              type="number"
              value={enteredAmount}
              onChange={amountChangeHandler}
              min="0.01"
              step="0.01"
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              value={enteredDate}
              onChange={dateChangeHandler}
              min="2022-01-01"
              max="2028-12-31"
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Cancel</button> {/* Add an onClick listener*/}
          <button type="submit">Add Expense</button>
        </div>
      </form>
    );
  };

  const viewForm = (viewForm = true) => {
    if (viewForm === true) {
      return showExpenseForm;
    } else {
      return toggleAddExpense;
    }
  };

  return viewForm;

  // return (
  //   <form onSubmit={submitHandler}>
  //     <div className="new-expense__controls">
  //       <div className="new-expense__control">
  //         <label>Title</label>
  //         <input
  //           type="text"
  //           value={enteredTitle}
  //           onChange={titleChangeHandler}
  //         />
  //       </div>
  //       <div className="new-expense__control">
  //         <label>Amount</label>
  //         <input
  //           type="number"
  //           value={enteredAmount}
  //           onChange={amountChangeHandler}
  //           min="0.01"
  //           step="0.01"
  //         />
  //       </div>
  //       <div className="new-expense__control">
  //         <label>Date</label>
  //         <input
  //           type="date"
  //           value={enteredDate}
  //           onChange={dateChangeHandler}
  //           min="2022-01-01"
  //           max="2028-12-31"
  //         />
  //       </div>
  //     </div>
  //     <div className="new-expense__actions">
  //       <button type="submit">Cancel</button>  {/* Add an onClick listener*/}
  //       <button type="submit">Add Expense</button>
  //     </div>
  //   </form>
  // );
};

export default ExpenseForm;
